import React from "react";
import { BtnGoBack } from "../../components/btns/index";
import modalRight from "../../assets/modal-right.png";

const Modal = () => {
  const [firstName, setFirstName] = React.useState("");
  const [lastName, setLastName] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [phone, setPhone] = React.useState("");

  const handleSubmit = () => {
    const data = {
      first_name: firstName,
      last_name: lastName,
      email: email,
      phone: phone,
    };
    console.log(data)
  };

  return (
    <div className="modal__wrapper">
      <div className="modal">
        <div className="col-6 modal__left">
          <div>
            <BtnGoBack />
          </div>
          <h3 className="modal__title">Registraton</h3>
          <h6 className="modal__suptitle">Add main information about user</h6>
          <form className="modal__form">
            <label className="modal__label">
              <input
                required
                onChange={(event) => setFirstName(event.target.value)}
                value={firstName}
                type="text"
                placeholder="First name"
              />
              <p>First name</p>
            </label>
            <label className="modal__label">
              <input
                required
                onChange={(event) => setLastName(event.target.value)}
                value={lastName}
                type="text"
                placeholder="Last name"
              />
              <p>Last name</p>
            </label>
            <label className="modal__label">
              <input
                required
                onChange={(event) => setEmail(event.target.value)}
                value={email}
                type="text"
                placeholder="Email"
              />
              <p>Email</p>
            </label>
            <label className="modal__label">
              <input
                required
                onChange={(event) => setPhone(event.target.value)}
                value={phone}
                type="text"
                placeholder="Phone number"
              />
              <p>Phone number</p>
            </label>
            <button className="btn-blue form__btn" onSubmit={() => handleSubmit()}>Add New User</button>
          </form>
        </div>
        <div className="col-6 text-right">
          <img className="modal__right" src={modalRight} alt="modalRight" />
        </div>
      </div>
    </div>
  );
};

export default Modal;
